#!/usr/bin/env ruby -wKU

def escape(choice)
  choice.gsub(/[,|\\$]/, '\\\\\0')
end

choices = %x{ "${TM_GIT:-git}" log --pretty=tformat:'%s' -n15 }.split(/\n/)
choices.reject! { |choice| choice =~ /^(fixup|squash)\b/ }
choices.map! { |choice| escape choice }

STDOUT << "fixup! ${1|#{choices.join ','}|}"
